    _subFromTotalDebt(amount);
    vaultDebt[vaultID] = userNewDebt;
  }

  /**
   * @dev Adds debt to the vault
   *
   * Requirements:
   * - new user debt cannot be above debt ceiling
   */
  function _addToTotalDebt(uint256 amount) internal {
